CREATE DEFINER=`root`@`localhost` PROCEDURE `calculateDailyRevenue`(

    IN input_day INT, IN input_month INT, IN input_year INT

)
BEGIN

    DECLARE startDate DATETIME;

    DECLARE endDate DATETIME;



    SET startDate = STR_TO_DATE(CONCAT(input_year,'-',input_month,'-',input_day), '%Y-%m-%d');

    SET endDate = DATE_ADD(startDate, INTERVAL 1 DAY);



    SELECT SUM(ct.price * ct.quantity + o.order_ship_cost) AS totalAmount

    FROM Orders o

             JOIN order_items ct ON o.id = ct.order_id

    WHERE DATE(o.date_created) >= startDate AND DATE(o.date_created) < endDate;

END ;;
