CREATE DEFINER=`root`@`localhost` PROCEDURE `RevenueMonthComparison`(

    IN input_month INT, IN input_year INT

)
BEGIN



    DECLARE previousMonthRevenue DECIMAL(65, 2);

    DECLARE currentMonthRevenue DECIMAL(65, 2);

    DECLARE revenuePercentage DECIMAL(65, 2);

    DECLARE monthNow INT;

    DECLARE monthLast INT;



    SET previousMonthRevenue = (SELECT SUM(ct.price * ct.quantity + o.order_ship_cost) AS totalAmount

                                FROM Orders o

                                         JOIN order_items ct ON o.id = ct.order_id

                                WHERE DATE_FORMAT(o.date_created, '%Y-%m') = DATE_FORMAT(

                                        DATE_SUB(DATE_FORMAT(CONCAT(input_year, '-', input_month, '-01'), '%Y-%m-%d'),

                                                 INTERVAL 1 MONTH), '%Y-%m')

                                  AND payment_status = 1);



    SET currentMonthRevenue = (SELECT SUM(ct.price * ct.quantity + o.order_ship_cost) AS totalAmount

                               FROM Orders o

                                        JOIN order_items ct ON o.id = ct.order_id

                               WHERE DATE_FORMAT(o.date_created, '%Y-%m') =

                                     DATE_FORMAT(CONCAT(input_year, '-', input_month, '-01'), '%Y-%m')

                                 AND payment_status = 1);



    -- Get current month and last month

    SET monthNow = input_month;

    SET monthLast = IF(input_month = 1, 12, input_month - 1);



    IF currentMonthRevenue IS NOT NULL THEN

        IF previousMonthRevenue IS NOT NULL AND previousMonthRevenue <> 0 THEN

            SET revenuePercentage = (currentMonthRevenue - previousMonthRevenue) / previousMonthRevenue * 100;

        ELSE

            SET revenuePercentage = NULL;

        END IF;



        SELECT currentMonthRevenue AS currentMonthRevenue,

               revenuePercentage   AS revenuePercentage,

               monthNow            AS monthNow,

               monthLast           AS monthLast;

    ELSE

        SELECT 0         AS currentMonthRevenue,

               NULL      AS revenuePercentage,

               monthNow  AS monthNow,

               monthLast AS monthLast;

    END IF;

END ;;
